function getKeysOfTypeObject(object, withoutRelations = true) {
	var keysOfType = [];
	var keys = Object.keys(object);
	
	for (var i = 0; i < keys.length; i++) {
		if (keys[i][0].toUpperCase() == keys[i][0]) {
			if (withoutRelations) {
				if (keys[i].indexOf("Relation") == -1) {
					keysOfType.push(keys[i]);
				}
			} else {
				keysOfType.push(keys[i]);
			}
			
		}
	}

	return keysOfType;
}
