function beyondBaseline(text) {
	var pattern = /[g|j|p|q|y]/;
	
	if (text) {
		if(text.match(pattern)) {
		    return true;
		}
	}
	
	
	return false;
}
function hasCapitalLetters(text) {
	if (text == text.toUpperCase()) {
		 return true;
	}
	
	return false;
}
